import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Route, Link  } from 'react-router-dom';
import { createBrowserHistory } from 'history'
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AuthService from "./services/auth.service";

import Login from "./components/login.component";
import Register from "./components/register.component";
import Profile from "./components/profile.component";
import Home from "./components/home.component";
import BoardModerator from "./components/board-moderator.component";
import BoardAdmin from "./components/board-admin.component";
import ProductList from "./components/article-list.component";
import Product from "./components/article.component";
import AddProduct from "./components/add-article.component";
import Hooks from "./container/hook-component";
import FileUpload from "./container/upload-component";
import GlobalProvider from "./context/context";

class App extends Component{

    constructor(props){
        super(props);
        this.logOut = this.logOut.bind(this);

        this.state = {
            showAdminBoard : false,
            showModeratorBoard : false,
            currentUser : undefined,

        }

    }



    componentDidMount(){

        const user = AuthService.getCurrentUser();

        if (user) {
            this.setState({
                currentUser : user,
                showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
                showAdminBoard: user.roles.includes("ROLE_ADMIN")
            });
        }

    }

    logOut(){
        AuthService.logout();
       window.location.href="/";
     //   window.location.reload(false);
    }



    render(){

        const {currentUser, showModeratorBoard, showAdminBoard} = this.state;

        return(

            <Router history={createBrowserHistory }>

                    <div>
                        <nav className="navbar navbar-expand navbar-dark bg-dark">
                            <Link to="/" className="navbar-brand">
                            Simple App
                            </Link>
                            <div className="navbar-nav mr-auto">
                                <li className="nav-item">
                                    <Link to="/home" className="nav-link">
                                        Home
                                    </Link>
                                </li>

                                <li className="nav-item">
                                    <Link to="/hook" className="nav-link">
                                        Hooks
                                    </Link>
                                </li>

                                <li className="nav-item">
                                    <Link to="/upload" className="nav-link">
                                        Upload
                                    </Link>
                                </li>


                                {showAdminBoard && (
                                    <li className="nav-item">
                                    <Link to="/admin" className="nav-link">
                                        Admin Board
                                    </Link>
                                </li>
                                )}


                                {showModeratorBoard && (
                                    <li className="nav-item">
                                    <Link to="/mod" className="nav-link">
                                        Moderator Board
                                    </Link>
                                </li>
                                )}

                                {currentUser && (
                                <li className="nav-item">
                                <Link to="/article" className="nav-link">
                                    Products
                                </Link>
                                </li>

                                )}

                                {currentUser && (


                                <li className="nav-item">
                                <Link to="/add" className="nav-link">
                                    Add
                                </Link>
                                </li>
                                )}


                            </div>

                            {currentUser ? 
                            (
                            <div className="navbar-nav pull-right">
                                <li className="nav-item">
                                    <Link to="/profile" className="nav-link">
                                        {currentUser.username}
                                    </Link>
                                </li>

                                <li className="nav-item">
                                    <Link to="/" className="nav-link" onClick={this.logOut}>
                                        Logut
                                    </Link>
                                </li>

                            </div>
                                ) : (
                                <div className="navbar-nav  pull-right">
                                <li className="nav-item">
                                    <Link to="/login" className="nav-link">
                                        Login
                                    </Link>
                                </li>

                                <li className="nav-item">
                                    <Link to="/register" className="nav-link">
                                        Sign Up
                                    </Link>
                                </li>

                            </div>
                                ) 
                            }
                        </nav>

                        <div className="container mt-3">
                            <Switch>
                                <Route exact path={["/","/home"]} component={Home}/>
                                <Route exact path="/hook" component={Hooks}/>
                                <Route exact path="/upload" component={FileUpload}/>
                                <Route exact path="/login" component={Login}/>
                                <Route exact path="/register" component={Register}/>
                                <Route exact path="/profile" component={Profile}/>
                                <Route exact path="/mod" component={BoardModerator}/>
                                <Route exact path="/admin" component={BoardAdmin}/>
                                <Route exact path= "/article" component={ProductList} />
                                <Route exact path="/add" component={AddProduct} />
                                <Route path="/article/:id" component={Product} />
                            </Switch>
                        </div>

                    </div>


            </Router>
        );

    }

}

export default GlobalProvider(App);
//export default App;
